import React, { useCallback, useEffect, useState, useRef, ReactNode, ReactElement } from "react";
import * as SplashScreen from "expo-splash-screen";
import { View } from "react-native";
// 차후 폰트 변경 가능
import {
  useFonts,
  // NotoSansKR_100Thin,
  NotoSansKR_300Light,
  // NotoSansKR_400Regular,
  NotoSansKR_500Medium,
  NotoSansKR_700Bold
  // NotoSansKR_900Black
} from "@expo-google-fonts/noto-sans-kr";

type AppBootstrapProps = {
  children: ReactNode;
};

export default function AppBootstrap({ children }: AppBootstrapProps): ReactElement {
  const [appIsReady, setAppIsReady] = useState(false);
  const appIsReadyRef = useRef(false);
  const [fontLoaded] = useFonts({
    NotoSansKR_700Bold,
    NotoSansKR_500Medium,
    NotoSansKR_300Light
  });

  useEffect(() => {
    async function prepare() {
      try {
        await SplashScreen.preventAutoHideAsync();
        await new Promise(resolve => setTimeout(resolve, 1000));
        // console.log(appIsReady, "스플래시 1초 간 보여주는 중");
      } catch (e) {
        console.warn(e);
        // console.log(appIsReady, "에러 발생");
      } finally {
        appIsReadyRef.current = true;
        console.log(appIsReadyRef);
        // setAppIsReady(true);
        // console.log("setApp state", appIsReady);
      }
    }
    prepare();
  }, [appIsReadyRef.current]);

  // Splash 화면(1초)에서 필요 리소스 로딩 중인 화면으로 이동
  const onLayoutRootView = useCallback(async () => {
    // async function rootView() {

    if (appIsReadyRef.current && fontLoaded) {
      await SplashScreen.hideAsync();
      console.log("?");
    }
    return <>{children}</>;
  }, [appIsReadyRef.current]);

  if (!appIsReadyRef.current) {
    console.log({ appIsReadyRef }, "앱 리소스 다운로드에 실패했습니다.");
    return <></>;
  }
  if (!fontLoaded) {
    console.log({ fontLoaded }, "폰트를 불러오는 데 실패했습니다.");
    return <></>;
  }
  // rootView();
  console.log("어디지 지금");
  return <View onLayout={onLayoutRootView}></View>;
}
